#include "../test/include/t_sys.h"

#if defined(MX_CFG_CPU_ARCH_X86)

PVOID T_execGetCurSp ()
{
    PVOID sp;
    asm volatile ("mov %0, esp" : "=g" (sp));

    return  (sp);
}

VOID  T_execSetCurSp (PVOID pvSp)
{
    asm volatile ("mov esp, %0" :: "g" (pvSp));
}

#endif

